			_ısyeriDal = ısyeriDal;
		}

		public List<Isyeri> GetAllIsyeri() 
		{
			return _ısyeriDal.GetList();
		}

		public Isyeri GetIsyeriById(int Id) 
		{
			return _ısyeriDal.Get(x=>x.IsYeriId_PK == Id); 
		}

