		{
			return _ısyeriDal.Add(entity); 
		}

		public int Update(Isyeri entity) 
		{
			return _ısyeriDal.Update(entity); 
		}

		public int Delete(int Id) 
		{
			return _ısyeriDal.Delete(new Isyeri{IsYeriId_PK = Id}); 
		}

	}
